"""
Exceptions for SQA Logging Pytest Plugin
"""


class PytestMilanoError(Exception):
    """Generic Framework Error"""

    pass


class SQALoggingError(PytestMilanoError):
    """SQA Logging Error"""

    def __init__(self):
        self.message = f"Error found by module pytest-sqa-logging-{__version__}"


class NameParsingError(SQALoggingError):
    """Name Parsing Error"""

    def __init__(self, bad_name, reason):
        super().__init__()
        self.message += f"The name: {bad_name} could not be parsed.\n"
        self.message += f"Reason: {reason}"
